import React, { Component } from "react";
class FormHandling extends Component {
  state = {
    username: "",
  };
  render() {
    return (
      <div>
        <form>
          <label>User Name</label>
          <input type="text" value={this.state.username} />
        </form>
      </div>
    );
  }
}

export default FormHandling;
